// smartest programmer alive btw
      for (int i = 0; i < grid.length; i++) {
          for (int j = 0; j < grid[i].length; j++) {
              if (grid[i][j] == 'S') {
                  grid[i][j] = '|';  
              }
          }
      }

      int counter = 0;

      for (int i = 1; i < grid.length; i++) {

          // move beams down
          for (int j = 0; j < grid[i].length; j++) {
              if (grid[i-1][j] == '|' && grid[i][j] == '.') {
                  grid[i][j] = '|';
              }
          }

          // split only when a beam actually hits a splitter
          for (int j = 0; j < grid[i].length; j++) {
              if (grid[i][j] == '^' && grid[i-1][j] == '|') {

                  if (j - 1 >= 0 && grid[i][j-1] == '.') {
                      grid[i][j-1] = '|';
                      counter++;
                  }

                  if (j + 1 < grid[i].length && grid[i][j+1] == '.') {
                      grid[i][j+1] = '|';
                      counter++;
                  }
              }
          }
      }

      System.out.println(counter);
